```dart
import 'package:flutter/material.dart';
import 'package:permission_handler/permission_handler.dart';
import 'package:google_maps_flutter/google_maps_flutter.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Location Tracker',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: const MyHomePage(title: 'Location Tracking'),
    );
  }
}

class MyHomePage extends StatefulWidget {
  final String title;

  const MyHomePage({required this.title, super.key});

  @override
  State<MyHomePage> createState) => _MyHomePageState();
}

class _MyHomePageState extends State<MyHomePage] {
  late GoogleMapController mapController;
  double? latitude = 0.0;
  double? longitude = 0.0;

  // Function to check and request location permission
  Future<void> _checkLocationPermission() async {
    if (await Permission.location.isGranted) {
      // Already granted, get the location immediately.
      await _getCurrentPosition();
    } else if (await Permission.location.request().isGranted) {
      // Granted after requesting, get the location.
      await _getCurrentPosition();
    } else {
      // Not granted and not requested or denied with no explanation
      ScaffoldMessenger.of(context).showSnackBar(
        const SnackBar(content: Text('Location permission is required')),
      );
    }
  }

  // Function to get current position
  Future<void> _getCurrentPosition() async {
    final Position? position = await Geolocator.getCurrentPosition(
        desiredAccuracy: LocationAccuracy.high);

    if (position != null) {
      setState(() {
        latitude = position.latitude;
        longitude = position.longitude;
      });
    }
  }

  // Function to initialize the map controller
  @override
  void initState() {
    super.initState();
    _checkLocationPermission();
  }

  // Build the UI
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(widget.title),
      ),
      body: Stack(
        children: [
          GoogleMap(
            key: const Key('map'),
            initialCameraPosition: CameraPosition(
              target: LatLng(0, 0), // Default to some point until we get the location
              zoom: 15,
            ),
            onMapCreated: (GoogleMapController controller) {
              mapController = controller;
            },
          ),
          if (latitude != null && longitude != null)
            Positioned(
              bottom: 20,
              left: 20,
              child: Container(
                padding: const EdgeInsets.all(10),
                decoration: BoxDecoration(
                  color: Colors.white.withOpacity(0.8),
                  borderRadius: BorderRadius.circular(20),
                ),
                child: Text(
                    'Your location: ${latitude?.toStringAsFixed(6)}°, ${longitude?.toStringAsFixed(6)}°'),
              ),
            )
        ],
      ),
    );
  }
}
