class SynthesisFacility:
    def __init__(self, reactants):
        self.reactants = reactants

    def mix_reactants(self, reactant1, reactant2, catalyst=None):
        if not (reactant1 in self.reactants and reactant2 in self.reactants):
            raise ValueError("Reactants not available.")

        product = f"{reactant1} + {reactant2}"
        if catalyst:
            product += f" + {catalyst}"
        print(f"Mixing {reactant1}, {reactant2} (optional catalyst: {catalyst}) to produce {product}")

    def add_reactant(self, reactant):
        if reactant not in self.reactants:
            self.reactants.append(reactant)
            print(f"Added new reactant: {reactant}")

# Example usage:
